import { Router } from '@angular/router';
import { NgxSpinnerService } from 'ngx-spinner';
import { Component, OnInit, TemplateRef } from '@angular/core';
import { BsModalRef, BsModalService } from 'ngx-bootstrap/modal';
import { ToastrService } from 'ngx-toastr';
import { ProdutoService } from 'src/app/services/produto.service';
import { Produto } from 'src/app/models/Produto';
import { ProdutosComponent } from '../produtos.component';


@Component({
  selector: 'app-produto-lista',
  templateUrl: './produto-lista.component.html',
  styleUrls: ['./produto-lista.component.scss']
})
export class ProdutoListaComponent implements OnInit {

  modalRef: BsModalRef | undefined;
  public produtos: Produto[] = [];
  public produtosFiltrados: Produto[] = [];

  public larguraImagem = 150;
  public margemImagem = 2;
  public exibirImagem = true;
  private filtroListado = '';

  public get filtroLista(): string {
    return this.filtroListado;
  }

  public set filtroLista(value: string) {
    this.filtroListado = value;
    // this.produtosFiltrados = this.filtroLista ? this.produtosFiltrados(this.filtroLista) : this.produtos;
  }

  public filtrarEventos(filtrarPor: string): Produto[] {
    filtrarPor = filtrarPor.toLocaleLowerCase();
    return this.produtos.filter(
      evento => evento.nome.toLocaleLowerCase().indexOf(filtrarPor) !== -1 ||
      evento.descricao.toLocaleLowerCase().indexOf(filtrarPor) !== -1
    );
  }

  constructor(
    private eventoService: ProdutoService,
    private modalService: BsModalService,
    private toastr: ToastrService,
    // private spinner: NgxSpinnerService,
    private router: Router
  ) { }

  public ngOnInit(): void {
    // this.spinner.show();
    this.getProdutos();
  }

  public getProdutos(): void {
    this.eventoService.getEventos().subscribe({
      next: (eventos: Produto[]) => {
        this.produtos = eventos;
        this.produtosFiltrados = this.produtos;
      },
      error: (error: any) => {
        // this.spinner.hide();
        this.toastr.error('Erro ao Carregar os Produtos', 'Erro!');
      },
      // complete: () => this.spinner.hide()
    });
  }

  openModal(template: TemplateRef<any>): void {
    this.modalRef = this.modalService.show(template, {class: 'modal-sm'});
  }

  // confirm(): void {
  //   this.modalRef.hide();
  //   this.toastr.success('O Produto foi deletado com Sucesso.', 'Deletado!');
  // }

  // decline(): void {
  //   this.modalRef.hide();
  // }

  detalheProduto(id: number): void{
    this.router.navigate([`produtos/detalhe/${id}`]);
  }

}

<div class="card rounded shadow-sm p-3">

  <div class="d-flex">
    <div class="flex-fill pr-3">
      <div class="input-group mb-2">
        <div class="input-group-prepend">
          <div class="input-group-text">Filtro: </div>
        </div>
        <!-- <input
        type="text"
        class="form-control"
        placeholder="Filtrar por Nome e Descrição"
        [(ngModel)]="filtroLista"
        > -->
      </div>
    </div>
    <div>
      <a class="d-flex btn btn-outline-primary" routerLink="/produtosdetalhe">
        <i class="fa fa-plus-circle my-1"></i>
        <b class="ml-1 d-none d-sm-block">Novo</b>
      </a>
    </div>
    </div>
  <!-- <h3>Filtro: {{filtroLista}}</h3> -->
  <table class="table table-striped table-hover">
    <thead class="thead-dark">
      <tr>
        <!-- <th class="d-none d-md-table-cell">
          <button type="button" class="btn btn-outline-light btn-sm"
          (click)="alterarImagem()">
          <i class="{{!exibirImagem ? 'fa fa-eye' : 'fa fa-eye-slash'}}"></i>
          {{!exibirImagem ? 'exibir' : 'ocultar'}}
        </button> -->

      <th>#</th>
      <th>Nome</th>
      <th class="d-none d-md-table-cell">Preço</th>
      <th>Data</th>
      <th class="d-none d-md-table-cell">Quantidade</th>
      <th class="d-none d-md-table-cell">Lote</th>
      <th>Opções</th>
    </tr>
  </thead>
  <tbody *ngIf="produtos && produtos.length">
    <tr *ngFor="let produto of produtosFiltrados" (click)="detalheProduto(produto.id)" style="cursor: pointer;">
      <td class="d-none d-md-table-cell">

      </td>
      <td>{{produto.id}}</td>
      <td>{{produto.nome}}</td>
      <td class="d-none d-md-table-cell">{{produto.preco}}</td>
      <td>{{produto.dataCadastro}}</td>
      <td class="d-none d-md-table-cell">{{produto.quantidade}}</td>
      <td>
        <button type="button" class="btn btn-danger btn-lg" tooltip="Excluir" (click)="openModal(template)">
          <i class="fa fa-trash"></i>
        </button>
      </td>
    </tr>
  </tbody>
  <tfoot *ngIf="!produto.length">
    <tr>
      <td colspan="8" class="text-center">
        <h4>Nenhum produto encontrado!</h4>
      </td>
    </tr>
  </tfoot>
</table>

</div>

<!-- {{eventos | json}} -->

<ng-template #template>
  <div class="modal-body text-center">
    <p>Tem certeza que quer deletar este Produto?</p>
    <!-- <button type="button" class="btn btn-default" (click)="confirm()">Sim</button> -->
    <!-- <button type="button" class="btn btn-primary" (click)="decline()">Não</button> -->
  </div>
</ng-template>
